import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import UserService from '../../services/UserService';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrashAlt, faEye, faArrowRight } from '@fortawesome/free-solid-svg-icons';

export default function ListPlanComponent() {
  const [travelplans, setTravelPlans] = useState([]);
  const navigate = useNavigate();

  const registerUser = (travelPlanId) => {
    UserService.registerUserToTravelPlan(userIDtravelPlanId)
      .then(() => {
        // Update the UI to reflect the registration status if needed
        fetchData();
      })
      .catch((error) => {
        console.error('Error registering user:', error);
      });
  };

  const deregisterUser = (travelPlanId) => {
    UserService.deregisterUserFromTravelPlan(travelPlanId)
      .then(() => {
        // Update the UI to reflect the registration status if needed
        fetchData();
      })
      .catch((error) => {
        console.error('Error deregistering user:', error);
      });
  };

  const viewPlan = (id) => {
    navigate(`/view-plan/${id}`);
  };

  const fetchData = () => {
    UserService.getAllTravelPlans()
      .then((response) => {
        setTravelPlans(response.data);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div>
      <h2 className='text-center'>Travel Plans</h2>
      <div className='row'>
        {travelplans.map((travelPlan) => (
          <div className='col-md-4 mt-3' key={travelPlan.id}>
            <div className='card'>
              <img src={travelPlan.imageUrl} className='card-img-top' alt='Travel Plan' />
              <div className='card-body'>
                <h5 className='card-title'>
                  {travelPlan.origin} <FontAwesomeIcon icon={faArrowRight} /> {travelPlan.destination}
                </h5>
                <p className='card-text'>{travelPlan.description}</p>
                <p className='card-text'>
                  <strong>Start Date:</strong> {travelPlan.startDate}
                </p>
                <p className='card-text'>
                  <strong>End Date:</strong> {travelPlan.endDate}
                </p>
                <p className='card-text'>
                  <strong>Budget:</strong> {travelPlan.budget}
                </p>
                <div className='btn-group'>
                  <button
                    onClick={() => viewPlan(travelPlan.id)}
                    className='btn btn-info'
                  >
                    <FontAwesomeIcon icon={faEye} /> View
                  </button>
                  {travelPlan.registered ? (
                    <button
                      onClick={() => deregisterUser(travelPlan.id)}
                      className='btn btn-danger'
                    >
                      Deregister
                    </button>
                  ) : (
                    <button
                      onClick={() => registerUser(travelPlan.id)}
                      className='btn btn-success'
                    >
                      Register
                    </button>
                  )}
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
