import React, { useState, useEffect } from 'react';
import { useNavigate,useParams } from 'react-router-dom';
import EmployeeService from '../services/EmployeeService';
const CreatePlanComponent = () => {
  //step 2
  const { id } = useParams();
  const [origin, setOrigin]=useState('');
  const [destination,setDestination]=useState('');
  const [startdate, setStartDate] = useState('');
  const [enddate, setEndDate] = useState('');
  const [description, setDescripton] = useState('');
  const navigate = useNavigate();
  const handleFirstNameChange = (event) => {
    setFirstName(event.target.value);
  };
  const handleLastNameChange = (event) => {
    setLastName(event.target.value);
  };
  const handleEmailIdChange = (event) => {
    setEmailId(event.target.value);
  };
  //step 3
  useEffect(() => {
    // Fetch employee data when the component mounts
    //step 4
    if (id === -1) {
      return;
    } else {
      const fetchEmployeeData = async () => {
        try {
          const response = await EmployeeService.getEmployeeById(id);
          const employee = response.data;

          // Set the state with the fetched employee data
          setFirstName(employee.firstName);
          setLastName(employee.lastName);
          setEmailId(employee.emailId);
        } catch (error) {
          console.error('Error fetching employee data:', error);
        }
      };

      fetchEmployeeData();
    }
  }, [id]);

   const saveEmployee =(event)=>{
    event.preventDefault(); 
    let employeeData = {
        firstName: firstName,
        lastName: lastName,
        emailId: emailId
      };
      console.log('Employee Data:', JSON.stringify(employeeData));
      if (id === -1) {
        EmployeeService.createEmployee(employeeData).then((res)=>{
          navigate('/employees');
     });
      } else {
        EmployeeService.updateEmployee(employeeData,id).then(res=>{
          navigate("/employees");
       });
      }

   };
   const cancel=(event)=>{
    navigate('/employees');
   };
   const getTitle=(event)=>{
    if(id==-1){
      return <h3 className="text-center">Add Employee</h3>
    }
    else{
      return <h3 className="text-center">Update Employee</h3>
    }
   };
  return (
    <div>
      <div className="container">
        <div className="row">
          <div className="card col-md-6 offset-md-3">
           { getTitle()}
            <div className="card-body">
              <form>
                <div className="form-group">
                  <label htmlFor="firstName">First Name:</label>
                  <input
                    type="text"
                    className="form-control"
                    id="firstName"
                    placeholder="Enter first name"
                    value={firstName}
                    onChange={handleFirstNameChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="lastName">Last Name:</label>
                  <input
                    type="text"
                    className="form-control"
                    id="lastName"
                    placeholder="Enter last name"
                    value={lastName}
                    onChange={handleLastNameChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="firstName">Email ID:</label>
                  <input
                    type="email"
                    className="form-control"
                    id="emailId"
                    placeholder="Enter Email ID"
                    value={emailId}
                    onChange={handleEmailIdChange}
                  />
                </div>
                <button className='btn btn-success' onClick={saveEmployee}>Save</button>
                <button className='btn btn-danger' onClick={cancel} style={{marginLeft:"10px"}}>Cancel</button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CreateEmployeeComponent;
