import React, { createContext, useContext, useState, useEffect } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [userId, setUserId] = useState(null);
  const [token, setToken] = useState(null);

  const login = (userId, token) => {
    setUserId(userId);
    setToken(token);
    console.log('Before setting userId:', userId);
localStorage.setItem('userId', userId);
console.log('After setting userId:', localStorage.getItem('userId'));
    // localStorage.setItem('userId', userId);
    localStorage.setItem('token', token);
    console.log(userId);
  };

  const logout = () => {
    setUserId(null);
    setToken(null);
    localStorage.removeItem('userId');
    localStorage.removeItem('token');
  };

  useEffect(() => {
    // Attempt to retrieve the token and userId from localStorage on component mount
    const storedUserId = localStorage.getItem('userId');
    const storedToken = localStorage.getItem('token');
    if (storedUserId && storedToken) {
      setUserId(storedUserId);
      setToken(storedToken);
      console.log(storedUserId,storedToken)
    }
  }, []);

  return (
    <AuthContext.Provider value={{ storeuserId, login, logout, token }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  const { userId, login, logout, token } = context;

  const isAuthenticated = () => !!token;

  return { userId, login, logout, isAuthenticated, token };
};
