// CreatePlanComponent.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import AdminService from '../../services/AdminService';
import { toast, Toaster } from 'react-hot-toast';

const CreatePlanComponent = () => {
  const { id } = useParams();
  const [planData, setPlanData] = useState({
    origin: '',
    destination: '',
    startDate: '',
    endDate: '',
    description: '',
    budget: '',
    imageUrl: '',
  });

  const navigate = useNavigate();

  useEffect(() => {
    const fetchTravelPlanData = async () => {
      try {
        if (id && id !== "-1") {
          const response = await AdminService.getPlanById(id);
          const travelplan = response.data;

          setPlanData(travelplan);
        }
      } catch (error) {
        console.error('Error fetching travel data:', error);
      }
    };

    fetchTravelPlanData();
  }, [id]);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setPlanData((prevData) => ({ ...prevData, [name]: value }));
  };

  const saveTravelPlan = async (event) => {
    event.preventDefault();

    try {
      if (!id || id === "-1") {
        await AdminService.createPlan(planData);
        navigate('/admin-home/');
        toast.success('Travel plan created successfully!');
      } else {
        await AdminService.updatePlan(planData, id);
        navigate("/admin-home/");
        toast.success('Travel plan updated successfully!');
      }
    } catch (error) {
      console.error('Error:', error);
      toast.error('Error saving travel plan. Please try again.');
    }
  };

  const cancel = () => {
    navigate('/admin-home');
  };

  const getTitle = () => {
    return id === "-1" ? <h3 className="text-center">Add New Travel Plan</h3> : <h3 className="text-center">Update Existing Travel Plan</h3>;
  };

  return (
    <div>
      <div className="container">
        <div className="row">
          <div className="card col-md-6 offset-md-3">
            {getTitle()}
            <div className="card-body">
              <form>
                <div className="form-group">
                  <label htmlFor="origin">Origin City:</label>
                  <input
                    type="text"
                    className="form-control"
                    id="origin"
                    placeholder="Enter origin City name"
                    name="origin"
                    value={planData.origin}
                    onChange={handleChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="destination">Destination City:</label>
                  <input
                    type="text"
                    className="form-control"
                    id="destination"
                    placeholder="Enter destination city name"
                    name="destination"
                    value={planData.destination}
                    onChange={handleChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="startDate">Start Date:</label>
                  <input
                    type="date"
                    className="form-control"
                    id="startDate"
                    placeholder="Enter Start Date"
                    name="startDate"
                    value={planData.startDate}
                    onChange={handleChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="endDate">End Date:</label>
                  <input
                    type="date"
                    className="form-control"
                    id="endDate"
                    placeholder="Enter End Date"
                    name="endDate"
                    value={planData.endDate}
                    onChange={handleChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="description">Description:</label>
                  <textarea
                    type="text"
                    rows="3"
                    className="form-control"
                    id="description"
                    placeholder="Enter Description"
                    name="description"
                    value={planData.description}
                    onChange={handleChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="budget">Budget:</label>
                  <input
                    type="number"
                    className="form-control"
                    id="budget"
                    placeholder="Enter Budget"
                    name="budget"
                    value={planData.budget}
                    onChange={handleChange}
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="imageUrl">Image URL:</label>
                  <input
                    type="text"
                    className="form-control"
                    id="imageUrl"
                    placeholder="Enter Image URL"
                    name="imageUrl"
                    value={planData.imageUrl}
                    onChange={handleChange}
                  />
                </div>
                <button className='btn btn-success' onClick={saveTravelPlan}>Save</button>
                <button className='btn btn-danger' onClick={cancel} style={{ marginLeft: "10px" }}>Cancel</button>
              </form>
            </div>
          </div>
        </div>
      </div>
      <Toaster position="bottom-center" />
    </div>
  );
};

export default CreatePlanComponent;
