// ListPlanComponent.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import AdminService from '../../services/AdminService';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrashAlt, faEye, faArrowRight } from '@fortawesome/free-solid-svg-icons';
import ViewUsersComponent from './ViewUsersComponent'; // Import the new component

const ListPlanComponent = () => {
  const [travelplans, setTravelPlans] = useState([]);
  const [selectedPlanId, setSelectedPlanId] = useState(null);
  const navigate = useNavigate();

  const addPlan = () => {
    navigate('/add-plan/-1');
  };

  const deletePlan = (id) => {
    AdminService.deletePlan(id).then((res) => {
      setTravelPlans((prevPlans) =>
        prevPlans.filter((travelPlan) => travelPlan.id !== id)
      );
    });
  };

  const editPlan = (id) => {
    navigate(`/add-plan/${id}`);
  };

  const viewPlan = (id) => {
    setSelectedPlanId(id);
  };

  useEffect(() => {
    const fetchData = () => {
      AdminService.getAllPlans()
        .then((response) => {
          setTravelPlans(response.data);
        })
        .catch((error) => {
          console.error('Error fetching data:', error);
        });
    };

    fetchData();
  }, []);

  return (
    <div>
      <h2 className='text-center'>Travel Plans</h2>
      <div className='row'>
        <button className='btn btn-primary' onClick={addPlan}>
          Add Plan
        </button>
      </div>
      <div className='row'>
        {travelplans.map((travelPlan) => (
          <div className='col-md-4 mt-3' key={travelPlan.id}>
            <div className='card'>
              {/* ... existing card content ... */}
              <div className='card-body'>
                {/* ... existing card body ... */}
                <div className='btn-group'>
                  <button
                    onClick={() => editPlan(travelPlan.id)}
                    className='btn btn-info'
                  >
                    <FontAwesomeIcon icon={faEdit} /> Update
                  </button>
                  <button
                    onClick={() => deletePlan(travelPlan.id)}
                    className='btn btn-danger'
                  >
                    <FontAwesomeIcon icon={faTrashAlt} /> Delete
                  </button>
                  <button
                    onClick={() => viewPlan(travelPlan.id)}
                    className='btn btn-info'
                  >
                    <FontAwesomeIcon icon={faEye} /> View
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
      {selectedPlanId && (
        <ViewUsersComponent travelPlanId={selectedPlanId} />
      )}
    </div>
  );
};

export default ListPlanComponent;
