import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import EmployeeService from '../services/EmployeeService';
const CreatePlanComponent = () => {
    //step 2
    const { id } = useParams();
    const [origin, setOrigin] = useState('');
    const [destination, setDestination] = useState('');
    const [startdate, setStartDate] = useState('');
    const [enddate, setEndDate] = useState('');
    const [description, setDescription] = useState('');
    const [budget, setBudget] = useState('');
    const [imageurl, setImageURL] = useState('');
    const navigate = useNavigate();
    const handleOriginChange = (event) => {
        setOrigin(event.target.value);
    };
    const handleDestinationChange = (event) => {
        setDestination(event.target.value);
    };
    const handleStartdateChange = (event) => {
        setStartDate(event.target.value);
    };
    const handleEndDateChange = (event) => {
        setEndDate(event.target.value);
    };
    const handleDescriptionChange = (event) => {
        setDescription(event.target.value);
    };
    const handleBudgetChange = (event) => {
        setBudget(event.target.value);
    };
    const handleImageUrlChange = (event) => {
        setImageURL(event.target.value);
    };
    //step 3
    useEffect(() => {
        // Fetch employee data when the component mounts
        //step 4
        if (id === -1) {
            return;
        } else {
            const fetchTravelPlanData = async () => {
                try {
                    const response = await AdminService.getPlanById(id);
                    const travelplan = response.data;

                    // Set the state with the fetched employee data
                    setOrigin(travelplan.origin);
                    setDestination(travelplan.destination);
                    setStartDate(travelplan.startDate);
                    setEndDate(travelplan.endDate);
                    setDescription(travelplan.description);
                    setBudget(travelplan.budget);
                    setImageURL(travelplan.imageUrl);

                } catch (error) {
                    console.error('Error fetching employee data:', error);
                }
            };

            fetchTravelPlanData();
        }
    }, [id]);

    const saveTravelPlan = (event) => {
        event.preventDefault();
        let travelPlanData = {
            origin : origin,
            destination: destination,
            startDate:startdate,
            endDate: enddate,
            description:description,
            budget:budget,
            imageurl:imageurl
        };
        console.log('Travel Plan Data:', JSON.stringify(travelPlanData));
        if (id === -1) {
            AdminService.createPlan(travelPlanData).then((res) => {
                navigate('/employees');
            });
        } else {
            EmployeeService.updateEmployee(employeeData, id).then(res => {
                navigate("/employees");
            });
        }

    };
    const cancel = (event) => {
        navigate('/travelplans');
    };
    const getTitle = (event) => {
        if (id == -1) {
            return <h3 className="text-center">Add New Travel Plan</h3>
        }
        else {
            return <h3 className="text-center">Update Existing Travel Plan</h3>
        }
    };
    return (
        <div>
            <div className="container">
                <div className="row">
                    <div className="card col-md-6 offset-md-3">
                        {getTitle()}
                        <div className="card-body">
                            <form>
                                <div className="form-group">
                                    <label htmlFor="firstName">First Name:</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        id="firstName"
                                        placeholder="Enter first name"
                                        value={firstName}
                                        onChange={handleFirstNameChange}
                                    />
                                </div>
                                <div className="form-group">
                                    <label htmlFor="lastName">Last Name:</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        id="lastName"
                                        placeholder="Enter last name"
                                        value={lastName}
                                        onChange={handleLastNameChange}
                                    />
                                </div>
                                <div className="form-group">
                                    <label htmlFor="firstName">Email ID:</label>
                                    <input
                                        type="email"
                                        className="form-control"
                                        id="emailId"
                                        placeholder="Enter Email ID"
                                        value={emailId}
                                        onChange={handleEmailIdChange}
                                    />
                                </div>
                                <button className='btn btn-success' onClick={saveEmployee}>Save</button>
                                <button className='btn btn-danger' onClick={cancel} style={{ marginLeft: "10px" }}>Cancel</button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CreateEmployeeComponent;
