export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  const { userId, login, logout, token } = context; // Add token

  const isAuthenticated = () => !!token; // Add this function

  return { userId, login, logout, isAuthenticated, token }; 

};
