// UserPage.js
import React, { useState, useEffect } from 'react';
import UserService from '../../services/UserService';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEye, faArrowRight } from '@fortawesome/free-solid-svg-icons';

export default function UserPage() {
  const [userTravelPlans, setUserTravelPlans] = useState([]);
  const userId = 3; // Replace with the actual user ID

  const fetchUserTravelPlans = () => {
    UserService.getAllTravelPlans(userId)
      .then((response) => {
        setUserTravelPlans(response.data);
      })
      .catch((error) => {
        console.error('Error fetching user travel plans:', error);
      });
  };

  const handleRegister = (travelPlanId) => {
    UserService.registerUserToTravelPlan(userId, travelPlanId)
      .then(() => {
        // Refresh user travel plans after registration
        fetchUserTravelPlans();
      })
      .catch((error) => {
        console.error('Error registering user for travel plan:', error);
      });
  };

  const handleDeregister = (travelPlanId) => {
    UserService.deregisterUserFromTravelPlan(userId, travelPlanId)
      .then(() => {
        // Refresh user travel plans after deregistration
        fetchUserTravelPlans();
      })
      .catch((error) => {
        console.error('Error deregistering user from travel plan:', error);
      });
  };

  useEffect(() => {
    // Fetch user travel plans on component mount
    fetchUserTravelPlans();
  }, []);

  return (
    <div>
      <h2 className='text-center'>Your Travel Plans</h2>
      <div className='row'>
        {userTravelPlans.map((userTravelPlan) => (
          <div className='col-md-4 mt-3' key={userTravelPlan.id}>
            <div className='card'>
              <img
                src={userTravelPlan.travelPlan.imageUrl}
                className='card-img-top'
                alt='Travel Plan'
              />
              <div className='card-body'>
                <h5 className='card-title'>
                  {userTravelPlan.travelPlan.origin}{' '}
                  <FontAwesomeIcon icon={faArrowRight} /> {userTravelPlan.travelPlan.destination}
                </h5>
                <p className='card-text'>{userTravelPlan.travelPlan.description}</p>
                <p className='card-text'>
                  <strong>Start Date:</strong> {userTravelPlan.travelPlan.startDate}
                </p>
                <p className='card-text'>
                  <strong>End Date:</strong> {userTravelPlan.travelPlan.endDate}
                </p>
                <p className='card-text'>
                  <strong>Budget:</strong> {userTravelPlan.travelPlan.budget}
                </p>
                <div className='btn-group'>
                  <button
                    className='btn btn-info'
                    onClick={() => handleDeregister(userTravelPlan.travelPlan.id)}
                  >
                    Deregister
                  </button>
                  <button
                    className='btn btn-info'
                    onClick={() => console.log('View clicked')}
                  >
                    <FontAwesomeIcon icon={faEye} /> View
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
