import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import AdminService from '../../services/AdminService';

const CreatePlanComponent = () => {
  // Step 2
  const { id } = useParams();
  const [origin, setOrigin] = useState('');
  const [destination, setDestination] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [description, setDescription] = useState('');
  const [budget, setBudget] = useState('');
  const [imageURL, setImageURL] = useState('');
  const navigate = useNavigate();

  const handleOriginChange = (event) => {
    setOrigin(event.target.value);
  };

  const handleDestinationChange = (event) => {
    setDestination(event.target.value);
  };

  const handleStartDateChange = (event) => {
    setStartDate(event.target.value);
  };

  const handleEndDateChange = (event) => {
    setEndDate(event.target.value);
  };

  const handleDescriptionChange = (event) => {
    setDescription(event.target.value);
  };

  const handleBudgetChange = (event) => {
    setBudget(event.target.value);
  };

  const handleImageURLChange = (event) => {
    setImageURL(event.target.value);
  };

  // Step 3
  useEffect(() => {
    // Fetch travel plan data when the component mounts
    // Step 4
    if (!id || id === '-1') {
      return;
    } else {
      const fetchTravelPlanData = async () => {
        try {
          const response = await AdminService.getPlanById(id);
          const travelPlan = response.data;

          // Set the state with the fetched travel plan data
          setOrigin(travelPlan.origin);
          setDestination(travelPlan.destination);
          setStartDate(travelPlan.startDate);
          setEndDate(travelPlan.endDate);
          setDescription(travelPlan.description);
          setBudget(travelPlan.budget);
          setImageURL(travelPlan.imageUrl);
        } catch (error) {
          console.error('Error fetching travel plan data:', error);
        }
      };

      fetchTravelPlanData();
    }
  }, [id]);

  const saveTravelPlan = (event) => {
    event.preventDefault();
    const travelPlanData = {
      origin: origin,
      destination: destination,
      startDate: startDate,
      endDate: endDate,
      description: description,
      budget: budget,
      imageUrl: imageURL, // Updated the key to match the backend
    };

    console.log('Travel Plan Data:', JSON.stringify(travelPlanData));

    if (!id || id === '-1') {
      AdminService.createPlan(travelPlanData)
        .then((res) => {
          navigate('/travelplans');
        })
        .catch((error) => {
          console.error('Error creating travel plan:', error);
        });
    } else {
      AdminService.updatePlan(travelPlanData, id)
        .then((res) => {
          navigate('/travelplans');
        })
        .catch((error) => {
          console.error('Error updating travel plan:', error);
        });
    }
  };

  const cancel = (event) => {
    navigate('/travelplans');
  };

  const getTitle = () => {
    if (id === '-1') {
      return <h3 className="text-center">Add New Travel Plan</h3>;
    } else {
      return <h3 className="text-center">Update Existing Travel Plan</h3>;
    }
  };

  return (
    <div>
      <div className="container">
        <div className="row">
          <div className="card col-md-6 offset-md-3">
            {getTitle()}
            <div className="card-body">
              <form>
                {/* ... Your form fields ... */}
                <button className="btn btn-success" onClick={saveTravelPlan}>
                  Save
                </button>
                <button
                  className="btn btn-danger"
                  onClick={cancel}
                  style={{ marginLeft: '10px' }}
                >
                  Cancel
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CreatePlanComponent;
